import React from "react";
import Search from "./Search";
import Movie from "./Movie";

export default function Content() {
  const [movieData, setMovieData] = React.useState([]);

  const fetchData = (title) => {
    const apiKey = "b64be8fa";
    fetch(`http://www.omdbapi.com/?apikey=${apiKey}&s=${title}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.Search) {
          setMovieData(data.Search);
        } else {
          setMovieData([]);
        }
      });
  };

  movieData.forEach((movie) => {
    const apiKey = "b64be8fa";
    fetch(`http://www.omdbapi.com/?apikey=${apiKey}&i=${movie.imdbID}`)
      .then((response) => response.json())
      .then((detailedData) => {
        console.log(detailedData);
      });
  });

  return (
    <div className="content">
      <h1>Movies</h1>
      <Search onSearch={fetchData} />
      <Movie movieData={movieData} movieDataDetailed={detailedData} />
    </div>
  );
}
