import React from "react";

export default function Movie() {
  const [movieData, setMovieData] = React.useState("Search");

  const fetchData = (title) => {
    const apiKey = "b64be8fa";
    fetch(`http://www.omdbapi.com/?apikey=${apiKey}&t=${title}&`)
      .then((response) => response.json())
      .then((data) => setMovieData(data));
  };

  return (
    <div className="movie--container">
      <div className="movie--text">
        {movieData.Title && (
          <h1>
            {movieData.Title}{" "}
            <span className="movie--year">({movieData.Year})</span>
          </h1>
        )}

        <span>{movieData.Awards}</span>
        <span>{movieData.Runtime}</span>
        <span>{movieData.imdbRating}</span>
        <span>{movieData.Genre}</span>
        <p className="movie--plot">{movieData.Plot}</p>
      </div>
      <div className="movie--poster">
        <img src={movieData.Poster} alt={movieData.Title} />
      </div>
    </div>
  );
}
