import React from "react";
import Search from "./Search";
import MovieContainer from "./MovieContainer";

const [selectedMovie, setSelectedMovie] = React.useState(null);

function renderStarIcons(rating) {
  const starIcons = [];
  const totalStars = 5;

  for (let i = 1; i <= totalStars; i++) {
    if (i <= rating) {
      starIcons.push(<i key={i} className="fa-solid fa-star"></i>);
    } else if (i - 0.5 === rating) {
      starIcons.push(<i key={i} className="fa-solid fa-star-half-stroke"></i>);
    } else {
      starIcons.push(<i key={i} className="fa-regular fa-star"></i>);
    }
  }

  return starIcons;
}

function handleMovieReset(setSelectedMovie) {
  setSelectedMovie(null);
}

function handleMovieClick(movie) {
  setSelectedMovie(movie);
}

export default function Content() {
  const [movieData, setMovieData] = React.useState([]);

  const fetchData = (title) => {
    const apiKey = "b64be8fa";
    fetch(`http://www.omdbapi.com/?apikey=${apiKey}&s=${title}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.Search) {
          const imdbIDs = data.Search.map((movie) => movie.imdbID);
          const fetchPromises = imdbIDs.map((imdbID) =>
            fetch(
              `http://www.omdbapi.com/?apikey=${apiKey}&i=${imdbID}&plot=short`
            ).then((response) => response.json())
          );

          Promise.all(fetchPromises)
            .then((detailedDataArray) => {
              const combinedData = data.Search.map((movie, index) => ({
                ...movie,
                ...detailedDataArray[index],
              }));
              setMovieData(combinedData);
            })
            .catch((error) => {
              console.error("Error fetching detailed data:", error);
            });
        } else {
          setMovieData([]);
        }
      })
      .catch((error) => {
        console.error("Error fetching search results:", error);
      });
  };

  React.useEffect(() => {
    fetchData("");
  }, []);

  return (
    <div className="content">
      <h1>Movies</h1>
      <Search onSearch={fetchData} />
      <MovieContainer
        movieData={movieData}
        renderStarIcons={renderStarIcons}
        handleMovieReset={handleMovieReset}
      />
    </div>
  );
}
